package com.google.android.fhir.datacapture.views

import android.view.View
import android.widget.FrameLayout
import android.widget.TextView
import androidx.test.espresso.Espresso.onView
import androidx.test.espresso.action.ViewActions.click
import androidx.test.espresso.action.ViewActions.typeText
import androidx.test.espresso.assertion.ViewAssertions.matches
import androidx.test.espresso.matcher.RootMatchers.isPlatformPopup
import androidx.test.espresso.matcher.ViewMatchers.isDisplayed
import androidx.test.espresso.matcher.ViewMatchers.withId
import androidx.test.espresso.matcher.ViewMatchers.withText
import androidx.test.ext.junit.rules.ActivityScenarioRule
import androidx.test.platform.app.InstrumentationRegistry
import com.google.android.fhir.datacapture.R
import com.google.android.fhir.datacapture.TestActivity
import com.google.android.fhir.datacapture.extensions.EXTENSION_ITEM_ANSWER_MEDIA
import com.google.android.fhir.datacapture.utilities.showDropDown
import com.google.android.fhir.datacapture.validation.NotValidated
import com.google.android.fhir.datacapture.views.factories.DropDownViewHolderFactory
import com.google.android.fhir.datacapture.views.factories.QuestionnaireItemViewHolder
import com.google.android.material.textfield.MaterialAutoCompleteTextView
import com.google.common.truth.Truth.assertThat
import org.hl7.fhir.r4.model.Attachment
import org.hl7.fhir.r4.model.Coding
import org.hl7.fhir.r4.model.Extension
import org.hl7.fhir.r4.model.Questionnaire
import org.hl7.fhir.r4.model.QuestionnaireResponse
import org.hl7.fhir.r4.model.StringType
import org.junit.Before
import org.junit.Rule
import org.junit.Test

class DropDownViewHolderFactoryEspressoTest {
  @Rule
  @JvmField
  var activityScenarioRule: ActivityScenarioRule<TestActivity> =
    ActivityScenarioRule(TestActivity::class.java)

  private lateinit var parent: FrameLayout
  private lateinit var viewHolder: QuestionnaireItemViewHolder
  private val itemAnswerMediaExtension =
    Extension().apply {
      url = EXTENSION_ITEM_ANSWER_MEDIA
      setValue(
        Attachment().apply {
          data =
            "".toByteArray()
          contentType = "image/png"
        }
      )
    }

  @Before
  fun setup() {
    activityScenarioRule.scenario.onActivity { activity -> parent = FrameLayout(activity) }
    viewHolder = DropDownViewHolderFactory.create(parent)
    setTestLayout(viewHolder.itemView)
  }

  @Test
  fun shouldClearAutoCompleteTextView() {
    val questionnaireViewItem =
      QuestionnaireViewItem(
        answerOptions("Coding 1", "Coding 2", "Coding 3", "Coding 4", "Coding 5"),
        responseOptions(),
        validationResult = NotValidated,
        answersChangedCallback = { _, _, _, _ -> },
      )
    runOnUI { viewHolder.bind(questionnaireViewItem) }

    onView(withId(R.id.auto_complete)).perform(showDropDown())
    onView(withText("-")).inRoot(isPlatformPopup()).check(matches(isDisplayed())).perform(click())
    assertThat(viewHolder.itemView.findViewById<TextView>(R.id.auto_complete).text.toString())
      .isEqualTo("-")
    assertThat(questionnaireViewItem.answers).isEmpty()
  }

  @Test
  fun shouldSetDropDownValueToAutoCompleteTextView() {
    var answerHolder: List<QuestionnaireResponse.QuestionnaireResponseItemAnswerComponent>? = null
    val questionnaireViewItem =
      QuestionnaireViewItem(
        answerOptions("Coding 1", "Coding 2", "Coding 3", "Coding 4", "Coding 5"),
        responseOptions(),
        validationResult = NotValidated,
        answersChangedCallback = { _, _, answers, _ -> answerHolder = answers },
      )
    runOnUI { viewHolder.bind(questionnaireViewItem) }

    onView(withId(R.id.auto_complete)).perform(showDropDown())
    onView(withText("Coding 3"))
      .inRoot(isPlatformPopup())
      .check(matches(isDisplayed()))
      .perform(click())
    assertThat(viewHolder.itemView.findViewById<TextView>(R.id.auto_complete).text.toString())
      .isEqualTo("Coding 3")
    assertThat((answerHolder!!.single().value as Coding).display).isEqualTo("Coding 3")
  }

  @Test
  fun shouldClearAutoCompleteTextViewOnRebindingView() {
    var answerHolder: List<QuestionnaireResponse.QuestionnaireResponseItemAnswerComponent>? = null
    val questionnaireViewItem =
      QuestionnaireViewItem(
        answerOptions("Coding 1", "Coding 2", "Coding 3", "Coding 4", "Coding 5"),
        responseOptions(),
        validationResult = NotValidated,
        answersChangedCallback = { _, _, answers, _ -> answerHolder = answers },
      )
    runOnUI { viewHolder.bind(questionnaireViewItem) }

    onView(withId(R.id.auto_complete)).perform(showDropDown())
    onView(withText("Coding 3"))
      .inRoot(isPlatformPopup())
      .check(matches(isDisplayed()))
      .perform(click())
    assertThat(viewHolder.itemView.findViewById<TextView>(R.id.auto_complete).text.toString())
      .isEqualTo("Coding 3")

    runOnUI { viewHolder.bind(questionnaireViewItem) }
    assertThat(viewHolder.itemView.findViewById<TextView>(R.id.auto_complete).text.toString())
      .isEqualTo("")
    val autoCompleteTextView =
      viewHolder.itemView.findViewById(R.id.auto_complete) as MaterialAutoCompleteTextView
    assertThat(autoCompleteTextView.compoundDrawablesRelative[0]).isNull()
    assertThat(autoCompleteTextView.compoundDrawablesRelative[1]).isNull()
    assertThat(autoCompleteTextView.compoundDrawablesRelative[2]).isNull()
    assertThat(autoCompleteTextView.compoundDrawablesRelative[3]).isNull()
  }

  @Test
  fun shouldSetImageToAutoCompleteTextViewOnItemSelection() {
    var answerHolder: List<QuestionnaireResponse.QuestionnaireResponseItemAnswerComponent>? = null
    val questionnaireViewItem =
      QuestionnaireViewItem(
        createAnswerOptions(
          "Coding 1",
          "Coding 2",
          "Coding 3",
          "Coding 4",
          "Coding 5",
          hasImageExtension = true
        ),
        responseOptions(),
        validationResult = NotValidated,
        answersChangedCallback = { _, _, answers, _ -> answerHolder = answers },
      )
    runOnUI { viewHolder.bind(questionnaireViewItem) }

    onView(withId(R.id.auto_complete)).perform(showDropDown())
    onView(withText("Coding 3"))
      .inRoot(isPlatformPopup())
      .check(matches(isDisplayed()))
      .perform(click())
    assertThat(viewHolder.itemView.findViewById<TextView>(R.id.auto_complete).text.toString())
      .isEqualTo("Coding 3")
    val autoCompleteTextView =
      viewHolder.itemView.findViewById(R.id.auto_complete) as MaterialAutoCompleteTextView
    assertThat(autoCompleteTextView.compoundDrawablesRelative[0]).isNotNull()
    assertThat(autoCompleteTextView.compoundDrawablesRelative[1]).isNull()
    assertThat(autoCompleteTextView.compoundDrawablesRelative[2]).isNull()
    assertThat(autoCompleteTextView.compoundDrawablesRelative[3]).isNull()
  }

  @Test
  fun shouldSetDropDownValueStringToAutoCompleteTextView() {
    var answerHolder: List<QuestionnaireResponse.QuestionnaireResponseItemAnswerComponent>? = null
    val questionnaireViewItem =
      QuestionnaireViewItem(
        createAnswerOptions("Coding 1", "Coding 2", "Coding 3", "Coding 4", "Coding 5"),
        responseValueStringOptions(),
        validationResult = NotValidated,
        answersChangedCallback = { _, _, answers, _ -> answerHolder = answers },
      )
    runOnUI { viewHolder.bind(questionnaireViewItem) }

    onView(withId(R.id.auto_complete)).perform(showDropDown())
    onView(withText("Coding 1"))
      .inRoot(isPlatformPopup())
      .check(matches(isDisplayed()))
      .perform(click())
    assertThat(viewHolder.itemView.findViewById<TextView>(R.id.auto_complete).text.toString())
      .isEqualTo("Coding 1")
    assertThat((answerHolder!!.single().value as StringType).valueAsString).isEqualTo("Coding 1")
  }

  @Test
  fun shouldReturnNonFilteredDropDownMenuItems() {
    val questionnaireViewItem =
      QuestionnaireViewItem(
        createAnswerOptions("Coding 1", "Coding 2", "Coding 3", "Add", "Subtract"),
        responseValueStringOptions(),
        validationResult = NotValidated,
        answersChangedCallback = { _, _, _, _ -> },
      )
    runOnUI { viewHolder.bind(questionnaireViewItem) }

    onView(withId(R.id.auto_complete)).perform(showDropDown())
    assertThat(
        viewHolder.itemView
          .findViewById<MaterialAutoCompleteTextView>(R.id.auto_complete)
          .adapter.count
      )
      .isEqualTo(6) // +1 cause of '-' menu item
  }
  @Test
  fun shouldReturnFilteredDropDownMenuItems() {
    val questionnaireViewItem =
      QuestionnaireViewItem(
        createAnswerOptions("Coding 1", "Coding 2", "Coding 3", "Add", "Subtract"),
        responseValueStringOptions(),
        validationResult = NotValidated,
        answersChangedCallback = { _, _, _, _ -> },
      )
    runOnUI { viewHolder.bind(questionnaireViewItem) }

    onView(withId(R.id.auto_complete)).perform(showDropDown())
    onView(withId(R.id.auto_complete)).perform(typeText("Coding"))
    assertThat(
        viewHolder.itemView
          .findViewById<MaterialAutoCompleteTextView>(R.id.auto_complete)
          .adapter.count
      )
      .isEqualTo(3)
  }

  @Test
  fun shouldReturnFilteredWithNoResultsDropDownMenuItems() {
    val questionnaireViewItem =
      QuestionnaireViewItem(
        createAnswerOptions("Coding 1", "Coding 2", "Coding 3", "Add", "Subtract"),
        responseValueStringOptions(),
        validationResult = NotValidated,
        answersChangedCallback = { _, _, _, _ -> },
      )
    runOnUI { viewHolder.bind(questionnaireViewItem) }

    onView(withId(R.id.auto_complete)).perform(showDropDown())
    onView(withId(R.id.auto_complete)).perform(typeText("Division"))
    assertThat(
        viewHolder.itemView
          .findViewById<MaterialAutoCompleteTextView>(R.id.auto_complete)
          .adapter.count
      )
      .isEqualTo(0)
  }

  /** Method to run code snippet on UI/main thread */
  private fun runOnUI(action: () -> Unit) {
    activityScenarioRule.scenario.onActivity { action() }
  }

  /** Method to set content view for test activity */
  private fun setTestLayout(view: View) {
    activityScenarioRule.scenario.onActivity { activity -> activity.setContentView(view) }
    InstrumentationRegistry.getInstrumentation().waitForIdleSync()
  }

  private fun answerOptions(vararg options: String) =
    Questionnaire.QuestionnaireItemComponent().apply {
      options.forEach { option ->
        addAnswerOption(
          Questionnaire.QuestionnaireItemAnswerOptionComponent().apply {
            value =
              Coding().apply {
                code = option.replace(" ", "_")
                display = option
              }
          }
        )
      }
    }

  private fun responseOptions(vararg responses: String) =
    QuestionnaireResponse.QuestionnaireResponseItemComponent().apply {
      responses.forEach { response ->
        addAnswer(
          QuestionnaireResponse.QuestionnaireResponseItemAnswerComponent().apply {
            value = Coding().apply { display = response }
          }
        )
      }
    }

  private fun createAnswerOptions(vararg options: String, hasImageExtension: Boolean = false) =
    Questionnaire.QuestionnaireItemComponent().apply {
      options.forEach { option ->
        addAnswerOption(
          Questionnaire.QuestionnaireItemAnswerOptionComponent().apply {
            if (hasImageExtension) {
              extension = listOf(itemAnswerMediaExtension)
            }
            value = StringType().apply { valueAsString = option }
          }
        )
      }
    }

  private fun responseValueStringOptions(vararg responses: String) =
    QuestionnaireResponse.QuestionnaireResponseItemComponent().apply {
      responses.forEach { response ->
        addAnswer(
          QuestionnaireResponse.QuestionnaireResponseItemAnswerComponent().apply {
            value = StringType().apply { valueAsString = response }
          }
        )
      }
    }
}
